import Details from "../Details/Details";
const Header = ({guestName, numberOfGuests, handleYesResponse, handleNoResponse, handlePersonClick, guestChoices}) => {
    if (!guestName || guestName.length === 0) {
        return <p>No names passed.</p>;
    }
    return (
        <>
        <h1 className="heading"> {guestName} You‚Äôre Invited!</h1>
        <p>Please RSVP to celebrate our wedding with us üíç</p> 
    <Details/> 
        <hr/>
        <img src="shotton.jpeg" alt="shotton" />
        <hr/> 
        <div className="button-group">
            {numberOfGuests > 1 ?
                <div>
{guestName[0].split(',').map((name, index) => {
  const isDone = guestChoices && guestChoices[name]; // true if guest has completed
  return (
    <p
      onClick={() => handlePersonClick(name)}
      key={index}
      className={`guest-name ${isDone ? 'done' : ''}`}
    >
      {name} {isDone && <span className="checkmark">‚úÖ</span>}
    </p>
  );
})}
                </div>
            : <><button
            type="button"
            onClick={handleYesResponse}
            className="btn"
            >
            Accept üéâ
            </button>

            <button
            type="button"
            onClick={handleNoResponse}
            className="btn"
            >
            Decline üíî
            </button></>}

        </div> 
        </>
    )
}

export default Header;
