export default function WeddingRSVPNight({}){
      const [answer, setAnswer] = useState("");
    const handleChange = (e) => {
        console.log(e)
        setAnswer(e.target.value);
    }
    return (
                  <><div className={answer === 'yes' ? "option btn-active" : "option"}>
            <label htmlFor="yes">
                <input type="radio" id="yes" name="nightChoice" value="yes" handleChange={handleChange} />
                I would love too!
            </label>
        </div><div className={answer === 'no' ? "option btn-active" : "option"}>
                <label htmlFor="no">
                    <input type="radio" id="no" name="nightCHhoice" value="no" handleChange={handleChange} />
                    Sorry i cannot make it 
                </label>
            </div></>
    )
}
