import { useState } from "react";
import "./WeddingRSVP.css";

export default function WeddingRSVP() {
  const [name, setName] = useState("");
  const [song, setSong] = useState("");
  const [response, setResponse] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const rsvpData = { name, guests, response };

    try {
      const res = await fetch("http://localhost:8080/api/rsvps", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rsvpData),
      });

      if (res.ok) {
        setSubmitted(true);
      } else {
        alert("Failed to submit RSVP. Try again.");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting RSVP.");
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="heading">You‚Äôre Invited!</h1>
        <p className="subtitle">Please RSVP to celebrate our wedding with us üíç</p>

        <form onSubmit={handleSubmit} className="form">
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            className="input"
          />

          We would like you to choose a song to add to our evening playlist

          <input
            type="text"
            placeholder="Enter your choice here"
            value={song}
            onChange={(e) => setSong(e.target.value)}
            required
            className="input"
          />

          <div className="button-group">
            <button
              type="button"
              onClick={() => setResponse("yes")}
              className={`btn ${response === "yes" ? "btn-active" : ""}`}
            >
              Accept üéâ
            </button>

            <button
              type="button"
              onClick={() => setResponse("no")}
              className={`btn ${response === "no" ? "btn-active" : ""}`}
            >
              Decline üíî
            </button>
          </div>

          <button
            type="submit"
            disabled={!response}
            className={`submit-btn ${!response ? "disabled" : ""}`}
          >
            Submit RSVP
          </button>
        </form>
      </div>
    </div>
  );
}
