        <p>Please RSVP to celebrate our wedding with us üíç</p> 
    <Details/> 
        <hr/>
        <img src="shotton.jpeg" alt="shotton" />
        <hr/> 
        <div className="button-group">
            {numberOfGuests > 1 ?
                <div>
{guestName[0].split(",").map((name, index) => {
  const response = guestChoices?.[name]?.response; 
  // response will be: true / false / undefined

  return (
    <p
      onClick={() => handlePersonClick(name)}
      key={index}
      className={`guest-name ${
        response === true ? "done" : response === false ? "not-done" : ""
      }`}
    >
      {name}{" "}
      {response === true && <span className="checkmark">‚úîÔ∏è</span>}
      {response === false && <span className="cross">‚ùå</span>}
    </p>
  );
})}
    <p>All children will have the same food choices</p>
    <p>Chicken Goujons, Fries, Peas</p>
                </div>
            : <><button
            type="button"
            onClick={handleYesResponse}
            className="btn"
            >
            Accept üéâ
            </button>

            <button
            type="button"
            onClick={handleNoResponse}
            className="btn"
            >
            Decline üíî
            </button></>}

        </div> 
        </>
    )
}

export default Header;
