import { useEffect, useState } from "react";
import WeddingRSVP from "./WeddingRSVP";
import Header from "./components/Header/Header";

export default function App() {
  const [guestChoices, setGuestChoices] = useState({});
    const [names, setNames] = useState([]);
    const [response, setResponse] = useState(null);
    const [progress, setProgress] = useState(false)
    const [numberOfGuestsOnInvite, setNumberOfGuests] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [guestData, setGuestData] = useState([])
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const allNames = params.getAll('name');
    const numberOfGuestsOnInvite = params.get('number');
    setNames(allNames);
    setNumberOfGuests(Number(numberOfGuestsOnInvite))
  }, []);

  const handleYesResponse = () => {
    setResponse("yes")
    setProgress(true)
  }

  const handleNoResponse = () => {
    setResponse("no")
  }

  const handlePersonClick = (name) => {
    setSelectedPerson(name);
  }

  const setPersonChoices = (value) => {
    setGuestData(value)
    setSelectedPerson("");
  }

const handleSubmit = async () => {
  const API_URL = process.env.REACT_APP_API_URL;
  const rsvpArray = Object.values(guestChoices); // ✅ convert guestChoices object to array

  try {
    const res = await fetch(`${API_URL}/api/rsvps/bulk`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(rsvpArray),
    });

    if (res.ok) {
      alert("RSVPs submitted successfully!");
    } else {
      alert("Failed to submit RSVP. Try again.");
    }
  } catch (err) {
    console.error(err);
    alert("Error submitting RSVP.");
  }
};

  return (
    <div className="container">
    <div className="card">
      {!selectedPerson && (
       <Header guestName={names} numberOfGuests={Number(numberOfGuestsOnInvite)} handleYesResponse={handleYesResponse} handleNoResponse={handleNoResponse} handlePersonClick={handlePersonClick} guestChoices={guestChoices}  />        
      )}

{selectedPerson && (
  <WeddingRSVP
    name={selectedPerson} // pass single name
    numberOfGuests={Number(numberOfGuestsOnInvite)}
    response={response}
    progress={progress}
    setPersonChoices={(personData) => {
    setGuestChoices((prev) => ({
       ...prev,
       [personData.name]: personData,
    }));
     setSelectedPerson(null); // ✅ go back to guest list
  }}
    existingChoices={guestChoices[selectedPerson] || {}}
  />
)}
{Object.keys(guestChoices).length === names.length ? (
  <button onClick={handleSubmit}>Submit RSVP</button>
) : null}

    </div>
    </div>

  );
}
