import { useState } from "react";
import "./WeddingRSVP.css";

export default function WeddingRSVP() {
  const [name, setName] = useState("");
  const [song, setSong] = useState("");
  const [response, setResponse] = useState(null);
  const [drinksChoice, setDrinkChoice] = useState("");
  const [starter, setStarter] = useState("");
  const [main, setMain] = useState("");
  const [dessert, setDesert] = useState("");
  const [allergies, setAllergiesQ] = useState(false)
  const [allgeriesText, setAllergiesText] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const handleStarterChange = (e) => {
    setStarter(e.target.value);
  };

  const handleMainChange = (e) => {
    setMain(e.target.value);
  }

  const handleDessertChange = (e) => {
    setDesert(e.target.value);
  }

  const handleAllergiesChange = (e) => {
    setAllergiesText(e.target.value);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    const rsvpData = { name, song, response, drinksChoice, starter, main, dessert, allgeriesText };

    try {
      const res = await fetch("http://localhost:8080/api/rsvps", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rsvpData),
      });

      if (res.ok) {
        setSubmitted(true);
      } else {
        alert("Failed to submit RSVP. Try again.");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting RSVP.");
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="heading">You‚Äôre Invited!</h1>
        <p>Please RSVP to celebrate our wedding with us üíç</p>

        <div className="details">
          <p>Friday 24th July 2026</p>
          <p>Shotton Grange</p>
          <p>Newcastle</p>
          <p>NE13 6DF</p>
        </div>

        <hr/>

        <form onSubmit={handleSubmit} className="form">
          Would you like a alcoholic or non-alcoholic drink for the toast and wedding breakfast?

          <button
              type="button"
              onClick={() => setDrinkChoice('alcoholic')}
              className={`btn ${drinksChoice === "alcoholic" ? "btn-active" : ""}`}
            >
              Alcoholic
            </button>

            <button
              type="button"
              onClick={() => setDrinkChoice('non-alcoholic')}
              className={`btn ${drinksChoice === "non-alcoholic" ? "btn-active" : ""}`}
            >
              Non-alcoholic 
            </button>


          <h2>Food Choices</h2>
          <h3>Starters:</h3>
          <input type="radio" id="chikenSalad" name="starter" value="Smoked Chicken Caesar Salad, Gem Lettuce, Anchovies, Parmesan Shavings, Pancetta Crisp, Herb Croutes & Caesar Dressing" onChange={handleStarterChange}/>
          <label for="chikenSalad">Smoked Chicken Caesar Salad, Gem Lettuce, Anchovies, Parmesan Shavings, Pancetta Crisp, Herb Croutes & Caesar Dressing</label><br/>

          <input type="radio" id="tomatoPestoPepper" name="starter" value="Sun Dried Tomato & Pesto Arancini, Roasted Red Pepper Pesto, Rocket & Parmesan (v)" onChange={handleStarterChange}/>
          <label for="tomatoPestoPepper">Sun Dried Tomato & Pesto Arancini, Roasted Red Pepper Pesto, Rocket & Parmesan (v)</label><br/>

          <input type="radio" id="soup" name="starter" value="Classic Minestrone Soup, Saffron Aioli, Parmesan, Croutons (v)" onChange={handleStarterChange}/>
          <label for="soup">Classic Minestrone Soup, Saffron Aioli, Parmesan, Croutons (v)</label>

          <h3>Mains:</h3>

          <input type="radio" id="medTart" name="main" value="Mediterranean Tart, Feta Cheese, Cherry Tomato Compote, Balsamic Glaze (v)" onChange={handleMainChange}/>
          <label for="html">Mediterranean Tart, Feta Cheese, Cherry Tomato Compote, Balsamic Glaze (v)</label><br/>

          <input type="radio" id="chickenMain" name="main" value="Chicken Supreme, Black Pudding Spring Roll, Red Cabbage Puree, Fondant Potato, Whiskey Jus" onChange={handleMainChange} />
          <label for="html">Chicken Supreme, Black Pudding Spring Roll, Red Cabbage Puree, Fondant Potato, Whiskey Jus</label><br/>

          <input type="radio" id="beefMain" name="main" value="Braised Daube of Beef, Glazed Roasted Root Vegetables, White Onion Puree, Fondant Potato, Thyme Jus (GF)" onChange={handleMainChange}/>
          <label for="html">Braised Daube of Beef, Glazed Roasted Root Vegetables, White Onion Puree, Fondant Potato, Thyme Jus (GF)</label>

          <h3>Desserts:</h3>

          <input type="radio" id="STP" name="dessert" value="Sticky Toffee Pudding, Butterscotch Sauce, Vanilla Ice Cream (V)" onChange={handleDessertChange}/>
          <label for="html">Sticky Toffee Pudding, Butterscotch Sauce, Vanilla Ice Cream (V)</label>

          <input type="radio" id="chcoDessert" name="dessert" value="Chocolate and Hazelnut Terrine, Praline, Hazelnut & Tonka Bean Ice Cream (v)" onChange={handleDessertChange}/>
          <label for="html">Chocolate and Hazelnut Terrine, Praline, Hazelnut & Tonka Bean Ice Cream (v)</label>

          <input type="radio" id="cheesecake" name="dessert" value="Raspberry Cheesecake, Prosecco Jelly, Raspberry Gel (v)" onChange={handleDessertChange}/>
          <label for="html">Raspberry Cheesecake, Prosecco Jelly, Raspberry Gel (v)</label>

          <h3>Any Allergies?</h3>

          <div className="button-group">
            <button
              type="button"
              onClick={() => setAllergiesQ(true)}
              className={`btn ${response === "yes" ? "btn-active" : ""}`}
            >
              Yes
            </button>

            <button
              type="button"
              onClick={() => setAllergiesQ(false)}
              className={`btn ${response === "no" ? "btn-active" : ""}`}
            >
              No 
            </button>
            <br/>
            {allergies && (
              <textarea onChange={handleAllergiesChange}/>
            )}
          </div>

          We would like you to choose a song to add to our evening playlist

          <input
            type="text"
            placeholder="Enter your choice here"
            value={song}
            onChange={(e) => setSong(e.target.value)}
            required
            className="input"
          />

          <div className="button-group">
            <button
              type="button"
              onClick={() => setResponse("yes")}
              className={`btn ${response === "yes" ? "btn-active" : ""}`}
            >
              Accept üéâ
            </button>

            <button
              type="button"
              onClick={() => setResponse("no")}
              className={`btn ${response === "no" ? "btn-active" : ""}`}
            >
              Decline üíî
            </button>
          </div>

          <button
            type="submit"
            disabled={!response}
            className={`submit-btn ${!response ? "disabled" : ""}`}
          >
            Submit RSVP
          </button>
        </form>
      </div>
    </div>
  );
}
