import { useEffect, useState } from "react";
import WeddingRSVP from "./WeddingRSVP";
import Header from "./components/Header/Header";

export default function App() {
  const [guestChoices, setGuestChoices] = useState({});
    const [names, setNames] = useState([]);
    const [response, setResponse] = useState(null);
    const [progress, setProgress] = useState(false)
    const [numberOfGuestsOnInvite, setNumberOfGuests] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [guestData, setGuestData] = useState([])
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const allNames = params.getAll('name');
    const numberOfGuestsOnInvite = params.get('number');
    setNames(allNames);
    setNumberOfGuests(Number(numberOfGuestsOnInvite))
  }, []);
  const [quizQuestion, setQuizQuestion ] = useState(false);
  const [questionTimed, showQuestionTimed] = useState(false);
  const [airmiles, setAirMiles] = useState();
  const [showQuiz, setShowQuiz] = useState(false);
  const [didTimerEnd, setDidTimerEnd] = useState(false);

  const handleYesResponse = () => {
    setResponse("yes")
    setProgress(true)
  }

  const handleNoResponse = () => {
    setResponse("no")
  }

  const handlePersonClick = (name) => {
    setSelectedPerson(name);
  }

  const setPersonChoices = (value) => {
    setGuestData(value)
    setSelectedPerson("");
  }

  const handleAirMilesChange = (e) => {
    setAirMiles(e.target.value)
  }

  useEffect(() => {
  let timer;
  if (quizQuestion) {
    timer = setTimeout(() => {
      setQuizQuestion(false); // hide the question
      setShowQuiz(false)
      setDidTimerEnd(true)
    }, 10000); // 20 seconds
  }

  return () => clearTimeout(timer); // cleanup if unmounted
}, [quizQuestion]);

const handleSubmit = async () => {
  const API_URL = process.env.REACT_APP_API_URL;
  const rsvpArray = Object.values(guestChoices); // ‚úÖ convert guestChoices object to array

  try {
    const res = await fetch(`${API_URL}/api/rsvps/bulk`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(rsvpArray),
    });

    if (res.ok) {
      alert("RSVPs submitted successfully!");
    } else {
      alert("Failed to submit RSVP. Try again.");
    }
  } catch (err) {
    console.error(err);
    alert("Error submitting RSVP.");
  }
};

  return (
  <div className="container">
    <div className="card">
      {!selectedPerson && (
        <Header
          guestName={names}
          numberOfGuests={Number(numberOfGuestsOnInvite)}
          handleYesResponse={handleYesResponse}
          handleNoResponse={handleNoResponse}
          handlePersonClick={handlePersonClick}
          guestChoices={guestChoices}
        />
      )}

      {selectedPerson && (
        <WeddingRSVP
          name={selectedPerson}
          numberOfGuests={Number(numberOfGuestsOnInvite)}
          response={response}
          progress={progress}
          setPersonChoices={(personData) => {
            setGuestChoices((prev) => ({
              ...prev,
              [personData.name]: personData,
            }));
            setSelectedPerson(null);
          }}
          existingChoices={guestChoices[selectedPerson] || {}}
        />
      )}

      {showQuiz && (
        <div className="quiz-section">
          {quizQuestion && !questionTimed && (
            <>
              <p>You have 20 seconds to answer the question, it is closest to the point, are you ready?</p>
              <button onClick={() => showQuestionTimed(true)}>YES!</button>
            </>
          )}

          {questionTimed && quizQuestion && (
            <div className="quizQuestionContainer">
              <p>Since Matty & Cherie met, how many air miles have they flown?</p>
              <input
                type="number"
                value={airmiles}
                onChange={(e) => handleAirMilesChange(e)}
                className="input"
              />
              <button>Submit Answer</button>
            </div>
          )}

          {questionTimed && !quizQuestion && <p>‚è∞ Time‚Äôs up! Thanks for playing!</p>}
        </div>
      )}

      {Object.keys(guestChoices).length === numberOfGuestsOnInvite && !showQuiz && !didTimerEnd && (
        <>
          <p>Would you like to take part in our fun quiz? Winner gets Champagne!! üçæ</p>
          <button onClick={() => setQuizQuestion(true)}>YES!</button>

          {quizQuestion && (
            <button onClick={() => setShowQuiz(true)}>Start Fun Quiz üéâ</button>
          )}


        </>
      )}

      {Object.keys(guestChoices).length === numberOfGuestsOnInvite && (
        <div className="submit-container">
          <button onClick={handleSubmit}>SUBMIT RSVP</button>
         </div>
      )}
    </div>
  </div>
);

}
