import { useEffect, useState } from "react";
import WeddingRSVP from "./WeddingRSVP";
import Header from "./components/Header/Header";

export default function App() {
    const [names, setNames] = useState([]);
    const [response, setResponse] = useState(null);
    const [numberOfGuestsOnInvite, setNumberOfGuests] = useState([]);
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const allNames = params.getAll('name');
    const numberOfGuestsOnInvite = params.get('number');
    setNames(allNames);
    setNumberOfGuests(Number(numberOfGuestsOnInvite))
  }, []);

  const handleYesResponse = () => {
    setResponse("yes")
    setProgress(true)
  }

  return (
    <div className="container">
    <div className="card">
       <Header guestName={names} handleYesResponse={handleYesResponse()}/>
      {Array.from({ length: numberOfGuestsOnInvite }).map((_, i) => (
        <WeddingRSVP names={names} numberOfGuests={Number(numberOfGuestsOnInvite)} />
      ))}
    </div>
    </div>

  );
}
