import Details from "../Details/Details";
import { useState } from "react";
const Header = ({guestName, numberOfGuests, handleYesResponse, handleNoResponse}) => {
      const [checked, setChecked] = useState({});

  const handleChange = (name) => {
    setChecked((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };
    return (
        <>
        <h1 className="heading"> {guestName} You‚Äôre Invited!</h1>
        <p>Please RSVP to celebrate our wedding with us üíç</p> 
    <Details/> 
        <hr/>
        <img src="shotton.jpeg" alt="shotton" />
        <hr/> 
        <div className="button-group">
            {numberOfGuests > 1 ?
                <div>
                    {guestName[0].split(',').map((name, index) => (
                    <label key={index} style={{ display: "block", marginBottom: "8px" }}>
                    {name}
                    <input
                        type="checkbox"
                        checked={!!checked[name]}
                        onChange={() => handleChange(name)}
                    />
                    </label>
                    ))}
                </div>
            : <><button
            type="button"
            onClick={handleYesResponse}
            className="btn"
            >
            Accept üéâ
            </button>

            <button
            type="button"
            onClick={handleNoResponse}
            className="btn"
            >
            Decline üíî
            </button></>}

        </div> 
        </>
    )
}

export default Header;
