import { useEffect, useState } from "react";
import WeddingRSVP from "./WeddingRSVP";

export default function App() {
    const [names, setNames] = useState([]);
    const [numberOfGuestsOnInvite, setNumberOfGuests] = useState([]);
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const allNames = params.getAll('name');
    console.log(params)
    const numberOfGuestsOnInvite = params.getAll('number');
    setNames(allNames);
    setNumberOfGuests(numberOfGuestsOnInvite)
  }, []);
  return (
    <div>
      {names.length === 0 && <p>No guests specified.</p>}
      {names.map((n, i) => (
        <WeddingRSVP key={i} guestName={n} numberOfGuests={numberOfGuestsOnInvite} />
      ))}
    </div>
  );
}
