import { useState } from "react";
import Details from "../Details/Details";
const Header = ({guestName, numberOfGuests, handleYesResponse, handleNoResponse, handlePersonClick, guestChoices, childOnInvite}) => {
    const [chidrenAnswer, setChildrenAnswer] = useState("");
    if (!guestName || guestName.length === 0) {
        return <p>No names passed.</p>;
    }

    const handleChildrenQuestion = (e) => {
        setChildrenAnswer(e.target.value);
    };

    return (
        <>
        <h1 className="heading"> {guestName} You‚Äôre Invited!</h1>
        <p>Please RSVP to celebrate our wedding with us üíç</p> 
    <Details/> 
        <hr/>
        <img src="shotton.jpeg" alt="shotton" />
        <hr/> 
        <div className="button-group">
            {numberOfGuests > 1 ?
                <div>
{guestName[0].split(",").map((name, index) => {
  const response = guestChoices?.[name]?.response; 
  // response will be: true / false / undefined

  return (
    <p
      onClick={() => handlePersonClick(name)}
      key={index}
      className={`guest-name ${
        response === true ? "done" : response === false ? "not-done" : ""
      }`}
    >
      {name}{" "}
      {response === true && <span className="checkmark">‚úîÔ∏è</span>}
      {response === false && <span className="cross">‚ùå</span>}
    </p>
  );
})}
{childOnInvite === 'yes' && (
    <>
    <h4>All children will have the same food choices</h4><h4>Chicken Goujons, Fries, Peas</h4>
    <label for="children">Will your child/children be attending?</label>
    <select value={chidrenAnswer} onChange={handleChildrenQuestion}>
        <option>Yes, all</option>
        <option>Yes, just one</option>
        <option>No</option>
    </select>
    </>
)}

                </div>
            : <><button
            type="button"
            onClick={handleYesResponse}
            className="btn"
            >
            Accept üéâ
            </button>

            <button
            type="button"
            onClick={handleNoResponse}
            className="btn"
            >
            Decline üíî
            </button></>}

        </div> 
        </>
    )
}

export default Header;
