import { useEffect, useState } from "react";
import WeddingRSVP from "./WeddingRSVP";
import Header from "./components/Header/Header";

export default function App() {
    const [names, setNames] = useState([]);
    const [response, setResponse] = useState(null);
    const [progress, setProgress] = useState(false)
    const [numberOfGuestsOnInvite, setNumberOfGuests] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [guestData, setGuestData] = useState([])
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const allNames = params.getAll('name');
    const numberOfGuestsOnInvite = params.get('number');
    setNames(allNames);
    setNumberOfGuests(Number(numberOfGuestsOnInvite))
  }, []);

  const handleYesResponse = () => {
    setResponse("yes")
    setProgress(true)
  }

  const handleNoResponse = () => {
    setResponse("no")
  }

  const handlePersonClick = (name) => {
    setSelectedPerson(name);
  }

  const setPersonChoices = (value) => {
    setGuestData(value)
    setSelectedPerson("");
  }

  const handleSubmit = async () => {
    const API_URL = process.env.REACT_APP_API_URL;
    const rsvpData = { name, song, response, drinksChoice, starter, main, dessert, allgeriesText };

    try {
      const res = await fetch(`${API_URL}/api/rsvps`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rsvpData),
      });

      if (res.ok) {
        setSubmitted(true);
      } else {
        alert("Failed to submit RSVP. Try again.");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting RSVP.");
    }
  };

  return (
    <div className="container">
    <div className="card">
      {!selectedPerson && (
       <Header guestName={names} numberOfGuests={Number(numberOfGuestsOnInvite)} handleYesResponse={handleYesResponse} handleNoResponse={handleNoResponse} handlePersonClick={handlePersonClick}  />        
      )}
{selectedPerson && (
  <WeddingRSVP
    name={selectedPerson} // pass single name
    numberOfGuests={Number(numberOfGuestsOnInvite)}
    response={response}
    progress={progress}
    setPersonChoices={setPersonChoices}
  />
)}
<button onClick={() => handleSubmit()}>ddd</button>
    </div>
    </div>

  );
}
