    setSubmitted(true);
  };

  if (submitted) {
    return (
      <div className="container">
        <div className="card">
          <h1 className="title">Thank You!</h1>
          <p className="text">
            We‚Äôve received your RSVP. Looking forward to celebrating together!
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <h1 className="heading">You‚Äôre Invited!</h1>
        <p className="subtitle">Please RSVP to celebrate our wedding with us üíç</p>

        <form onSubmit={handleSubmit} className="form">
          <input
            type="text"
            placeholder="Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            className="input"
          />

          We would like you to choose a song to add to our evening playlist

          <input
            type="text"
            placeholder="Enter your choice here"
            value={song}
            onChange={(e) => setName(e.target.value)}
            required
            className="input"
          />

          <div className="button-group">
            <button
