import { useState } from "react";
import WeddingRSVP from "./WeddingRSVP";
import Submit from "./components/Submit/Submit";

export default function App() {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const rsvpData = { name, song, response, drinksChoice, starter, main, dessert, allgeriesText };

    try {
      const res = await fetch("http://localhost:8080/api/rsvps", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rsvpData),
      });

      if (res.ok) {
        setSubmitted(true);
      } else {
        alert("Failed to submit RSVP. Try again.");
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting RSVP.");
    }
  };

  return (
    <div>
      <WeddingRSVP />
      <Submit handleSubmit={handleSubmit} />
    </div>
  );
}
